const headers = {
  Accept: 'application/json',
  'Access-Control-Allow-Origin':'*'
};

export const fetchProjects = () => {

  return fetch(`http://localhost:5000/api/project?`, {
    headers,
    method: "GET",
  })
    .then(response => {
      if (response.ok) {
        //handleErrorResponse(response);
        response.json().then( (json) => {
          return json
        })
      }
    })
};

export const uploadImages = (data) => {
  var urls = []
  data.pictures.forEach((file) => {
    let formData = new FormData();
    formData.append('file', file);
    fetch("http://localhost:5000/api/project/uploadImage", 
    {
      headers: headers,
      method: "POST",
      body: formData
    }).then((res) => {
      if (res.ok) {
        res.json().then((response) => {
          urls.push(response.url)
          if(urls.length === data.pictures.length){
            createProject(urls, data.title, data.description)
          }
        });
      } else if (res.status == 401) {
        alert("Oops! ");
      }
    }, function (e) {
      alert("Error submitting form!");
    });
  })
}

export const createProject = (urls, title, description) => {
  let formData = new FormData();
  formData.append('title', title);
  formData.append('description', description);
  formData.append('pictures', urls);
  fetch("http://localhost:5000/api/project/uploadProject", 
  {
    headers: headers,
    method: "POST",
    body: formData
  }).then(function (res) {
    if (res.ok) {
      alert("Perfect! ");
    } else if (res.status == 401) {
      alert("Oops! ");
    }
  }, function (e) {
    alert("Error submitting form!");
  });
}
