import React from 'react';
import PropTypes from 'prop-types';
import ImageGallery from 'react-image-gallery';
import Grid from '@material-ui/core/Grid';
import Coverflow from 'react-coverflow';
import "react-image-gallery/styles/css/image-gallery.css";

const images =(pictures) => {
  if(pictures){
    return pictures.map( picture => {
      return {
        original: picture.url,
        thumbnail: picture.url,
      }
    })
  }
}

const styles = {
  showCaseContentLeft:{
  },
  showCaseContentRight:{
    paddingLeft:20,
    paddingRight: 20
  },
  description:{
    whiteSpace: 'pre-line'
  }
};

const ProjectView = ({
  project
}) => {
  return (
      <div>
        <Grid container spacing ={0} style={styles.showcaseContent}>
          <Grid item xs={12} sm={6} style={styles.showCaseContentLeft}>
          <Coverflow width="960" height="500"
    displayQuantityOfSide={2}
    navigation={false}
    enableScroll={true}
    clickable={true}
    active={0}
  >
    <div
      role="menuitem"
      tabIndex="0"
    >
      <img
        src='image/path'
        alt='title or description'
        style={{
          display: 'block',
          width: '100%',
        }}
      />
    </div>
    {project.pictures.map(function(picture, index){
      return (     
        <img src={picture} alt='title or description' data-action="http://andyyou.github.io/react-coverflow/"/>     
      )
    })}
  </Coverflow>
          </Grid>
          <Grid item xs={12} sm={6} style={styles.showCaseContentRight}>
            <h2>{project.title}</h2>
            <h4>{project.type}</h4>
            <p style={styles.description}>
            {project.description}
            </p>
          </Grid>
        </Grid>
      </div>
  );
};

export default ProjectView;

ProjectView.propTypes = {
  project: PropTypes.object.isRequired,
};
