import React from 'react';
import { connect } from 'react-redux';
import Showcase  from './Showcase';
import ProjectDialog from './ProjectDialog';
import FbImageLibrary from 'react-fb-image-grid'
import {
  getProjects,
  openProjectDialog,
  closeProjectDialog
} from './redux/actions';
import {
  projectsFound,
  isProjectDialogOpen
} from './redux/selectors';

const styles = {
  grid : {
    maxWidth: 700,
    marginLeft: 'auto',
    marginRight: 'auto',
  },

  thumbnail: {
    cursor:'pointer',
    maxWidth: '100',
    height: 'auto',
  }
};


export class Home extends React.Component {

  componentDidMount() {
    this.props.getProjects();
  }
  render(){
    const {
      projectsFound,
      openEditDeveloperDialog,
      closeEditDeveloperDialog,
      isProjectDialogOpen
    } = this.props

    return(
      <div>
        <Showcase>
        </Showcase>
        {/* <PortfolioCarousel
          projectsFound = {projectsFound}
          openProjectDialog = {openEditDeveloperDialog}
        /> */}
        <div style = {styles.grid}>
        <FbImageLibrary 
          images={ projectsFound.map( project => project.thumbnail )} 
          onClickEach={({src, index}) => openEditDeveloperDialog(index) }
        />
        </div>
        <ProjectDialog
          project={{}}
          showDialog={isProjectDialogOpen}
          closeDialog={closeEditDeveloperDialog}
          scroll='body'
        />
      </div>
    )
  }
}

const mapStateToProps = state => ({
  projectsFound: projectsFound(state),
  isProjectDialogOpen: isProjectDialogOpen(state)
});

const mapDispatchToProps = dispatch => ({
  getProjects: () => dispatch(getProjects()),
  openEditDeveloperDialog: index=> dispatch(openProjectDialog(index)),
  closeEditDeveloperDialog: () => dispatch(closeProjectDialog()),
});

export default connect(mapStateToProps, mapDispatchToProps)(Home);
