import { login } from '../../../utils/api'
import { 
  LOGIN,
  ACCESS_TOKEN
} from '../../../utils/constants'

export const loginUser = (user) => dispatch => {
  return login(user).then( token => {
    localStorage.setItem(ACCESS_TOKEN, token);
    dispatch({
      type: LOGIN,
      payload: token,
    })
  })
}
