import React from 'react';
import { connect } from 'react-redux';
import Showcase  from './Showcase';
import PortfolioCarousel from './PortfolioCarousel';
import ProjectDialog from './ProjectDialog';
import {
  getProjects,
  openProjectDialog,
  closeProjectDialog
} from './redux/actions';
import {
  projectsFound,
  isProjectDialogOpen
} from './redux/selectors';

export class Home extends React.Component {

  componentDidMount() {
    this.props.getProjects();
    console.log(projectsFound)
  }

  constructor(props) {
    super(props);
    this.state = {
      showProjectDialog: false
    };
  }

  openProjectDialog(){
    this.setState({showProjectDialog: true})
  }

  closeProjectDialog(){
    this.setState({showProjectDialog: false})
  }

  render(){
    const {
      projectsFound
    } = this.props
    return(
      <div>
        <Showcase>
        </Showcase>
        <PortfolioCarousel
          projectsFound = {projectsFound}
          openProjectDialog = {() => this.openProjectDialog() }
        />
        <ProjectDialog
          project={{}}
          showDialog={this.state.showProjectDialog}
          closeDialog={() => this.closeProjectDialog()}
          scroll='body'
        />

      </div>
    )
  }
}

const mapStateToProps = state => ({
  projectsFound: projectsFound(state),
  isProjectDialogOpen: isProjectDialogOpen(state)
});

const mapDispatchToProps = dispatch => ({
  getProjects: () => dispatch(getProjects()),
  openEditDeveloperDialog: projectData=> dispatch(openProjectDialog(projectData)),
  closeEditDeveloperDialog: () => dispatch(closeProjectDialog()),
});

export default connect(mapStateToProps, mapDispatchToProps)(Home);
