import React, { Component } from 'react';
import logo from '../logo.svg';
import { connect } from 'react-redux';
import Background from '../assets/background1.png';
import TextField from '@material-ui/core/TextField';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import {DropzoneArea} from 'material-ui-dropzone';
import Button from '@material-ui/core/Button';
import { zIndex } from 'material-ui/styles';
import {ProjectForm} from './admin/ProjectForm';

const styles = {
  showcase : {
    height: '80vh',
    backgroundImage: `url(${Background})`,
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
    backgroundSize: 'cover'
  },
  overlay: {
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    height: '100%',
    width: '100%',
    display: 'flex',
    aligItems: 'center',
  },
  showcaseBanner: {
    marginTop: 'auto',
    marginBottom: 'auto',
    marginLeft: 50,
  },
  showcaseBannerText: {
    fontSize: 50,
    fontFamily: 'Roboto Mono',
    color:'#FFFFFF',
  },

  paper: {
    maxWidth: 1000,
    marginLeft: 'auto',
    marginRight: 'auto',
    paddingRight: 40,
    paddingLeft: 40,
    height: '100%'
  },
  field: {
    marginRight: 20,
    marginLeft: 20
  },
  container:{
    marginRight: 40,
    marginLeft: 40,
    position: 'relative',
  },
  dropzone:{
    marginTop: 20,
  },
  submitButton:{
    marginTop: 20,
    marginBottom: 20,
  },
  red:{
    background: 'red',
    height: '100%'
  },
  blue:{
    background: 'blue',
    height: '100%'
  }
}

class Home extends Component {

  constructor(props) {
    super(props);
    this.state = {
      title: '',
      description: '',
      pictures: []
    };
  }

  handleChange = fieldName => event => {
    this.setState({ 
      [fieldName] : event.target.value
    })
  };

  handleImageDrop = fieldName => event => {
    this.setState({ 
      [fieldName] : event
    })
  }

  createProject = () =>  {
    let formData = new FormData();
    formData.append('title', this.state.title);
    formData.append('description', this.state.description);
    formData.append('pictures', this.state.pictures);
    fetch("http://localhost:5000/api/project/uploadProject", 
    {
      headers: {
        'Content-Type': "multipart/form-data boundary --- WebKit193844043-h"
      },
      mode: 'no-cors',
      method: "POST",
      body: formData
    }).then(function (res) {
      if (res.ok) {
        alert("Perfect! ");
      } else if (res.status == 401) {
        alert("Oops! ");
      }
    }, function (e) {
      alert("Error submitting form!");
    });
  }
  render(){
    return(
      <div>
        <div style = {styles.showcase}> 
          <div style = {styles.overlay}>
          <Grid container spacing ={0} style={styles.paper}>
          <Grid item xs={3} sm={6} style={styles.red}>
                </Grid>
                <Grid item xs={3} sm={6} style={styles.blue}>
                </Grid>
            <Grid item xs={12} style={styles.container}>
              <Paper style = {styles.paper}>
              </Paper>
            </Grid>
          </Grid>
            {/* <Grid container spacing = {24} style = {styles.showcaseBanner}>
              <Grid item xs= {12} sm={6}>
                <h1 style = {styles.showcaseBannerText}>Christopher Ho Portfolio</h1>
              </Grid>
              <Grid container spacing = {24}>
                <Grid item xs={3} sm={2}>
                  <Button 
                  variant="contained" 
                  color="primary" 
                  onClick={() => { this.createProject()}}>
                    Create Project
                  </Button>
                </Grid>
                <Grid item xs={3} sm={2}>
                  <Button 
                  variant="contained" 
                  color="primary" 
                  onClick={() => { this.createProject()}}>
                    Create Project
                  </Button>
                </Grid>
              </Grid>
            </Grid> */}
          </div>
        </div>
          {/* <Grid container spacing = {24}>
            <Grid item xs={12} style={styles.container}>
              <Paper style = {styles.paper}>
                <Grid item xs= {12}>
                  <TextField
                    id="outlined-name"
                    label="Name"
                    value={this.state.name}
                    onChange={this.handleChange('title')}
                    margin="normal"
                    variant="outlined"
                    fullWidth={true}
                  />
                </Grid>
                <Grid item xs= {12}>
                  <TextField
                    id="outlined-description"
                    label="Description"
                    value={this.state.description}
                    onChange={this.handleChange('description')}
                    margin="normal"
                    variant="outlined"
                    multiline={true}
                    fullWidth={true}
                  />
                </Grid>
                <Grid item xs= {12}style = {styles.dropzone}>
                  <DropzoneArea 
                    onDrop={this.handleImageDrop('pictures')}
                    />
                </Grid>
                <Grid item xs= {12}>
                  <Button 
                  variant="contained" 
                  color="primary" 
                  style={styles.submitButton}
                  onClick={() => { this.createProject()}}>
                    Create Project
                  </Button>
                </Grid>
              </Paper>
            </Grid>
          </Grid> */}
      </div>
    )
  }
}

export default connect(null, null)(Home);
