import React from 'react';
import { connect } from 'react-redux';
import Showcase  from './Showcase';
import PortfolioCarousel from './PortfolioCarousel';
import ProjectDialog from './ProjectDialog';
import GridList from '@material-ui/core/GridList';
import GridListTile from '@material-ui/core/GridListTile';
import {
  getProjects,
  openProjectDialog,
  closeProjectDialog
} from './redux/actions';
import {
  projectsFound,
  isProjectDialogOpen
} from './redux/selectors';

const styles = {

  thumbnail: {
    cursor:'pointer'
  }
};

export class Home extends React.Component {

  componentDidMount() {
    this.props.getProjects();
  }

  render(){
    const {
      projectsFound,
      openEditDeveloperDialog,
      closeEditDeveloperDialog,
      isProjectDialogOpen
    } = this.props
    return(
      <div>
        <Showcase>
        </Showcase>
        {/* <PortfolioCarousel
          projectsFound = {projectsFound}
          openProjectDialog = {openEditDeveloperDialog}
        /> */}
        <GridList cellHeight={500} cols={4}>
          {projectsFound.map(project => (
            <GridListTile key={project.id} cols={1} onClick={() => this.selectPhoto(project.id)} styles={styles.thumbnail}>
              <img src={project.thumbnail} alt={project.title} styles={styles.thumbnail} />
            </GridListTile>
          ))}
        </GridList>
        <ProjectDialog
          project={{}}
          showDialog={isProjectDialogOpen}
          closeDialog={closeEditDeveloperDialog}
          scroll='body'
        />
      </div>
    )
  }
}

const mapStateToProps = state => ({
  projectsFound: projectsFound(state),
  isProjectDialogOpen: isProjectDialogOpen(state)
});

const mapDispatchToProps = dispatch => ({
  getProjects: () => dispatch(getProjects()),
  openEditDeveloperDialog: projectData=> dispatch(openProjectDialog(projectData)),
  closeEditDeveloperDialog: () => dispatch(closeProjectDialog()),
});

export default connect(mapStateToProps, mapDispatchToProps)(Home);
