import React, { Component } from 'react';
import logo from '../logo.svg';
import { connect } from 'react-redux';
import Background from '../assets/background1.png';
import InstagramProfilePhone from '../assets/instagramProfileMobile.png';
import TextField from '@material-ui/core/TextField';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import {DropzoneArea} from 'material-ui-dropzone';
import Button from '@material-ui/core/Button';
import { zIndex } from 'material-ui/styles';
import {ProjectForm} from './admin/ProjectForm';

const styles = {
  showcase : {
    height: '80vh',
    backgroundImage: `url(${Background})`,
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
    backgroundSize: 'cover'
  },
  overlay: {
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    height: '100%',
    width: '100%',
  },
  showcaseBannerText: {
    fontSize: 46,
    fontFamily: 'Roboto Mono',
    color:'#FFFFFF',
  },

  showcase: {
    maxWidth: 1000,
    marginLeft: 'auto',
    marginRight: 'auto',
    paddingRight: 40,
    paddingLeft: 40,
    height: '100%',
    display: 'flex',
    alignItems: 'center',
  },
  field: {
    marginRight: 20,
    marginLeft: 20
  },
  container:{
    marginRight: 40,
    marginLeft: 40,
    position: 'relative',
  },
  dropzone:{
    marginTop: 20,
  },
  submitButton:{
    marginRight: 20
  },
  red:{
    backgroundImage: `url(${InstagramProfilePhone})`,
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
    backgroundSize: 'cover',
    height: '100%',
  },
  blue:{
    height: '100%',
    paddingTop: 250,
    paddingLeft: 50
  }
}

class Home extends Component {

  constructor(props) {
    super(props);
    this.state = {
    };
  }

  render(){
    return(
      <div>
        <div style = {styles.showcase}> 
          <div style = {styles.overlay}>
            <Grid container spacing ={0} style={styles.showcase}>
              <Grid item xs={12} sm={6} style={styles.red}>
              </Grid>
              <Grid item xs={12} sm={6} style={styles.blue}>
                <Grid container spacing ={0}>
                  <Grid item xs={12}>
                    <h1 style={styles.showcaseBannerText}>Christopher Ho Portfolio</h1>
                  </Grid>
                </Grid>
                <Grid container spacing ={0}>
                  <Grid item xs={3}>
                    <Button 
                    variant="contained" 
                    color="primary" 
                    style={styles.submitButton}
                    onClick={() => {}}>
                      Resume
                    </Button>
                  </Grid>
                  <Grid item xs={3}>
                    <Button 
                    variant="contained" 
                    color="primary" 
                    style={styles.submitButton}
                    onClick={() => {}}>
                      Github
                    </Button>
                  </Grid>
                </Grid>
              </Grid>
            </Grid>
          </div>
        </div>
      </div>
    )
  }
}

export default connect(null, null)(Home);
