import {
  LOGIN,
  FETCH_PROJECTS_FOR_DASHBOARD
} from '../../../utils/constants';

export const defaultState = {
  isLoggedIn: false,
  projectsFound : []
};

const admin = (state = defaultState, action) => {
  switch (action.type) {
    case LOGIN:
      return {
        ...state,
        isLoggedIn: true
      };
    case FETCH_PROJECTS_FOR_DASHBOARD:
      return {
        ...state,
        projectsFound : action.payload
      };
    default:
      return state;
  }
};

export default admin;
