import { login, fetchProjects, deleteProject, uploadImage, createProject } from '../../../utils/api'
import { 
  LOGIN,
  LOGOUT,
  ACCESS_TOKEN,
  FETCH_PROJECTS_FOR_DASHBOARD,
  OPEN_DELETE_PROJECT_DIALOG,
  CLOSE_DELETE_PROJECT_DIALOG,
  SHOW_PROGRESS_OVERLAY,
  HIDE_PROGRESS_OVERLAY,
  SHOW_SNACKBAR,
  HIDE_SNACKBAR
} from '../../../utils/constants'
import history from '../../../utils/history'

export const loginUser = (user) => dispatch => {
  login(user).then( token => {
    if(token !== undefined){
      dispatch({
        type: LOGIN,
      })
      localStorage.setItem(ACCESS_TOKEN, token)
      history.push("/dashboard")
    } else {
      //handleError
    }
  })
}

export const logoutUser = () => dispatch => {
      dispatch({
        type: LOGOUT,
      })
      localStorage.removeItem(ACCESS_TOKEN)
      history.push("/login")
}

export const deleteSelectedProject = (projectId) => dispatch => {
  deleteProject(projectId).then( success => {
    if(success){
      window.location.reload()
    } else {
      // handle error
    }
  })
}

export const getProjects = () => dispatch => {
  return fetchProjects().then(projects => {
    dispatch({
      type: FETCH_PROJECTS_FOR_DASHBOARD,
      payload: projects,
    })
  })
}

export const openDeleteProjectDialog = projectId => dispatch => {
  dispatch({
    type: OPEN_DELETE_PROJECT_DIALOG,
    payload: projectId,
  });
};

export const closeDeleteProjectDialog = () => dispatch => {
  dispatch({
    type: CLOSE_DELETE_PROJECT_DIALOG,
  });
};

export const createAProject = (project) => dispatch => {
  dispatch({
    type: SHOW_PROGRESS_OVERLAY,
  })
  var pictureUrls = []
  project.pictures.forEach((picture) => {
    uploadImage(picture).then(json => {
      pictureUrls.push(json.url)
      if(pictureUrls.length === project.pictures.length){
        uploadImage(project.thumbnail).then(json => {
          createProject(
            pictureUrls, 
            json.url,
            project.title,
            project.description,
            project.type
          ).then(json => {
            dispatch({
              type: HIDE_PROGRESS_OVERLAY,
            });
            history.push("/dashboard")
          }).catch(error => {
            // handle error
          })
        }).catch(error => {
          //handle error
        })
      }
    }).catch(error => {
      // handle error
    })
  })
}

export const handleError = (message) => dispatch => {
  dispatch({
    type: SHOW_SNACKBAR,
    payload: message
  })
}

export const hideSnackBar = () => dispatch => {
  dispatch({
    type: HIDE_SNACKBAR
  })
}
