import React from 'react';
import PropTypes from 'prop-types';
import ImageGallery from 'react-image-gallery';
import SimpleImageSlider from "react-simple-image-slider";
import Grid from '@material-ui/core/Grid';
import Coverflow from 'react-coverflow';
import "react-image-gallery/styles/css/image-gallery.css";

const images = (pictures) => {
  var images = []
  pictures.array.forEach(element => {
    images.push(
      {
        url: element.url
      }
    )
  });
  return images
  // if(pictures) {
  //   return pictures.map( picture => {
  //     return {
  //       url: picture.url
  //     }
  //   })
  // }
}

const styles = {
  showCaseContentLeft:{
  },
  showCaseContentRight:{
    paddingLeft:20,
    paddingRight: 20
  },
  description:{
    whiteSpace: 'pre-line'
  }
};

const ProjectView = ({
  project
}) => {

  // const images = [
  //   { url: "https://s3.ca-central-1.amazonaws.com/chrishoportfolio/1552858721212-lowlife2.jpg" },
  //   { url: "https://s3.ca-central-1.amazonaws.com/chrishoportfolio/1552858721212-lowlife2.jpg"  },
  //   { url: "https://s3.ca-central-1.amazonaws.com/chrishoportfolio/1552858721212-lowlife2.jpg"  }
  // ];
  return (
      <div>
        <Grid container spacing ={0} style={styles.showcaseContent}>
          <Grid item xs={12} sm={6} style={styles.showCaseContentLeft}>
          <SimpleImageSlider
                    width={896}
                    height={504}
                    images={images(project.pictures)}
                />
          </Grid>
          <Grid item xs={12} sm={6} style={styles.showCaseContentRight}>
            <h2>{project.title}</h2>
            <h4>{project.type}</h4>
            <p style={styles.description}>
            {project.description}
            </p>
          </Grid>
        </Grid>
      </div>
  );
};

export default ProjectView;

ProjectView.propTypes = {
  project: PropTypes.object.isRequired,
};
