import React from 'react';
import { connect } from 'react-redux';
import { withRouter } from 'react-router-dom';
import Grid from '@material-ui/core/Grid';
import Thumbnails  from './Thumbnails';
import ProjectView from './ProjectView';
import {
  getProjects,
  selectProject
} from './redux/actions';
import {
  projectsFound,
  currentSelectedProject
} from './redux/selectors';

const styles = {
  thumbnails: {
    backgroundColor: 'red'
  },
  container:{
    marginLeft: 'auto',
    marginRight: 'auto',
    textAlign: 'center'
  }
}

export class ProjectGallery extends React.Component {

  componentWillMount(){
    this.props.getProjects();
  }

  componentDidMount() {
    //this.props.selectProject(0);
  }

  render(){
    const {
      projectsFound,
      selectProject,
      currentSelectedProject,
      history
    } = this.props

    return(
      <div style={styles.container}>
        <h2>Project Gallery</h2>
        <Grid container spacing = {24} >   
          <Grid item xs={12} sm={2} style={styles.thumbnails}>
            <Thumbnails
              projects={projectsFound}
              selectProject={selectProject}
            />
          </Grid>
          <Grid item xs={12} sm={10}>
            <ProjectView
              project={currentSelectedProject}
            />
          </Grid>
        </Grid>
      </div>
    )
  }
}

const mapStateToProps = state => ({
  projectsFound: projectsFound(state),
  currentSelectedProject: currentSelectedProject(state),
});

const mapDispatchToProps = dispatch => ({
  getProjects: () => dispatch(getProjects()),
  selectProject: (index) => dispatch(selectProject(index))
});

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(ProjectGallery));
