import { 
  ACCESS_TOKEN
} from './constants'

const headers = new Headers ({
  'Content-Type' : 'application/json',
  'Access-Control-Allow-Origin':'*'
});

export const login = (user) => {
  return fetch("http://localhost:5000/api/auth/signin", 
  {
    headers: headers,
    method: "POST",
    body: JSON.stringify(user)
  })  
  .then(response => response.json())
  .then(data => data.accessToken)
}

export const fetchProjects = () => {

  return fetch(`http://localhost:5000/api/project?`, {
    headers,
    method: "GET",
  })
  .then(response => response.json())
  .then(data => data.content)    
};

export const uploadImages = (data) => {
  var urls = []
  data.pictures.forEach((file) => {
    let formData = new FormData();
    formData.append('file', file);
    fetch("http://localhost:5000/api/project/uploadImage", 
    {
      headers: headers,
      method: "POST",
      body: formData
    }).then((res) => {
      if (res.ok) {
        res.json().then((response) => {
          urls.push(response.url)
          if(urls.length === data.pictures.length){
            uploadThumbnail(urls, data)
          }
        });
      } else if (res.status == 401) {
        alert("Oops! ");
      }
    }, function (e) {
      alert("Error submitting form!");
    });
  })
}

export const uploadThumbnail = (urls, data) => {
  let formData = new FormData();
  formData.append('file', data.thumbnail);
  fetch("http://localhost:5000/api/project/uploadImage", 
  {
    headers: headers,
    method: "POST",
    body: formData
  }).then((res) => {
    if (res.ok) {
      res.json().then((response) => {
        createProject(urls, response.url, data.title, data.description, data.type)
      });
    } else if (res.status == 401) {
      alert("Oops! ");
    }
  }, function (e) {
    alert("Error submitting form!");
  });
}

export const createProject = (urls, thumbnail, title, description, type) => {
  let formData = new FormData();
  formData.append('title', title);
  formData.append('type', type);
  formData.append('thumbnail', thumbnail)
  formData.append('description', description);
  formData.append('pictures', urls);
  fetch("http://localhost:5000/api/project/uploadProject", 
  {
    headers: headers,
    method: "POST",
    body: formData
  }).then(function (res) {
    if (res.ok) {
      alert("Perfect! ");
    } else if (res.status == 401) {
      alert("Oops! ");
    }
  }, function (e) {
    alert("Error submitting form!");
  });
}
