import React from 'react';
import PropTypes from 'prop-types';
import ImageGallery from 'react-image-gallery';
import SimpleImageSlider from "react-simple-image-slider";
import Grid from '@material-ui/core/Grid';
import Coverflow from 'react-coverflow';
import "react-image-gallery/styles/css/image-gallery.css";


const styles = {
  description:{
    whiteSpace: 'pre-line'
  },
  image:{
    height: 200,
    width: 200,

    marginTop: 10,
  },
  imageContainer :{
    textAlign: 'center'
  }
};

const ProjectView = ({
  project
}) => {
  return (
      <div>
        <Grid container spacing ={0} >
          <Grid item xs={12} sm={12}>
            <Grid container spacing ={0} >
              {project.pictures? (project.pictures.map(function(picture, index){
                return (  
                  <Grid item xs={12} sm={6} style={styles.imageContainer}>        
                    <img src={picture.url} alt={project.title} style={styles.image}/>
                  </Grid>
                )
              })) : ( <div></div>) }
            </Grid>

          </Grid>

          <Grid item xs={12} sm={12} style={styles.showCaseContentRight}>
            <h2>{project.title}</h2>
            <h4>{project.type}</h4>
            <p style={styles.description}>
            {project.description}
            </p>
          </Grid>
        </Grid>
      </div>
  );
};

export default ProjectView;

ProjectView.propTypes = {
  project: PropTypes.object.isRequired,
};
