import React from 'react';
import { connect } from 'react-redux';
import TextField from '@material-ui/core/TextField';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import {DropzoneArea} from 'material-ui-dropzone';
import Button from '@material-ui/core/Button';

const styles = {
  paper: {
    maxWidth: 700,
    marginLeft: 'auto',
    marginRight: 'auto',
    paddingRight: 40,
    paddingLeft: 40,
  },
  field: {
    marginRight: 20,
    marginLeft: 20
  },
  container:{
    marginRight: 40,
    marginLeft: 40,
    position: 'relative',
  },
  dropzone:{
    marginTop: 20,
  },
  submitButton:{
    marginTop: 20,
    marginBottom: 20,
  },
};

export class ProjectForm extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      title: '',
      description: '',
      pictures: []
    };
  }

  handleChange = fieldName => event => {
    this.setState({ 
      [fieldName] : event.target.value
    })
  };

  handleImageDrop = fieldName => event => {
    var files = this.state.pictures
    files.push(event)
    this.setState({ 
      [fieldName] : files
    })
  }

  createProject(urls) {
    let formData = new FormData();
    formData.append('title', this.state.title);
    formData.append('description', this.state.description);
    formData.append('pictures', this.state.pictures);
    fetch("http://localhost:5000/api/project/uploadProject", 
    {
      headers: {
        'Access-Control-Allow-Origin':'*',
        "Accept": "application/json"
      },
      method: "POST",
      body: formData
    }).then(function (res) {
      if (res.ok) {
        alert("Perfect! ");
      } else if (res.status == 401) {
        alert("Oops! ");
      }
    }, function (e) {
      alert("Error submitting form!");
    });
  }

  uploadImages = () => {
    var urls = []
    var numberOfFilesToUpload = this.state.pictures.length
    this.state.pictures.forEach((file) => {
      let formData = new FormData();
      formData.append('file', file);
      fetch("http://localhost:5000/api/project/uploadImage", 
      {
        headers: {
          'Access-Control-Allow-Origin':'*',
          "Accept": "application/json"
        },
        method: "POST",
        body: formData
      }).then((res) => {
        if (res.ok) {
          res.json().then((response) => {
            urls.push(response.url)
            if(urls.length === numberOfFilesToUpload){
              let formData = new FormData();
              formData.append('title', this.state.title);
              formData.append('description', this.state.description);
              formData.append('pictures', urls);
              fetch("http://localhost:5000/api/project/uploadProject", 
              {
                headers: {
                  'Access-Control-Allow-Origin':'*',
                  "Accept": "application/json"
                },
                method: "POST",
                body: formData
              }).then(function (res) {
                if (res.ok) {
                  alert("Perfect! ");
                } else if (res.status == 401) {
                  alert("Oops! ");
                }
              }, function (e) {
                alert("Error submitting form!");
              });
            }
          });
        } else if (res.status == 401) {
          alert("Oops! ");
        }
      }, function (e) {
        alert("Error submitting form!");
      });
    })
  }

  render(){
    return (
      <div>
          <Grid container spacing = {24}>
            <Grid item xs={12} style={styles.container}>
              <Paper style = {styles.paper}>
                <Grid item xs= {12}>
                  <TextField
                    id="outlined-name"
                    label="Name"
                    value={this.state.name}
                    onChange={this.handleChange('title')}
                    margin="normal"
                    variant="outlined"
                    fullWidth={true}
                  />
                </Grid>
                <Grid item xs= {12}>
                  <TextField
                    id="outlined-description"
                    label="Description"
                    value={this.state.description}
                    onChange={this.handleChange('description')}
                    margin="normal"
                    variant="outlined"
                    multiline={true}
                    fullWidth={true}
                  />
                </Grid>
                <Grid item xs= {12}style = {styles.dropzone}>
                  <DropzoneArea 
                    onDrop={this.handleImageDrop('pictures')}
                    />
                </Grid>
                <Grid item xs= {12}>
                  <Button 
                  variant="contained" 
                  color="primary" 
                  style={styles.submitButton}
                  onClick={() => { this.uploadImages()}}>
                    Create Project
                  </Button>
                </Grid>
              </Paper>
            </Grid>
          </Grid>
      </div>
    );
  }
}

export default connect(null,null)(ProjectForm);
