import React from 'react';
import Slider from "react-slick";
import ProjectCard from './ProjectCard'

// docs
// https://react-slick.neostack.com/docs/example/adaptive-height

const styles = {
  card: {
    marginLeft: 25,
    marginRight: 25
  },
  media: {
    height: 400,
  },
}

class PortfolioCarousel extends React.Component {
  componentDidMount() {
    console.log(this.props.projectsFound);
  }
  render(){
    var settings = {
      className: "center",
      centerMode: true,
      dots: true,
      infinite: true,
      speed: 500,
      slidesToShow: 1,
      slidesToScroll: 1,
      variableWidth: true,
      adaptiveHeight: true,
    };
    const {
      projectsFound,
      openProjectDialog
    } = this.props
    return(
      <div>
        <h1>Projects</h1>
        <Slider {...settings}>
          {projectsFound.map((projectData, index) => {
              return (
              <div style={{ width: 600 }}>
                <ProjectCard
                  title = {projectData.title}
                  description = {projectData.description}
                  openProjectDialog = {openProjectDialog}
                />
              </div>
              );
            })}
        </Slider>
      </div>
    )
  }
}


export default PortfolioCarousel;
