import {ACCESS_TOKEN} from './constants'

const headers = new Headers ({
  'Access-Control-Allow-Origin':'*',
  'Content-Type': 'application/json'
});

const formImageUploadHeaders = new Headers ({
  'Access-Control-Allow-Origin':'*',
  'Authorization': 'Bearer ' + localStorage.getItem(ACCESS_TOKEN)
})

export const login = (user) => {
  headers.append('Content-Type', 'application/json')
  return fetch("http://localhost:5000/api/auth/signin", 
  {
    headers: headers,
    method: "POST",
    body: JSON.stringify(user)
  })  
  .then(response => response.json())
  .then(data => data.accessToken)
}

export const deleteProject = (projectId) => {
  if(localStorage.getItem(ACCESS_TOKEN)) {
    headers.append('Authorization', 'Bearer ' + localStorage.getItem(ACCESS_TOKEN))
  }
  return fetch("http://localhost:5000/api/project/deleteproject/" + projectId,
  {
    headers: headers,
    method: "DELETE",
  })  
  .then(response => response.ok)
};

export const fetchProjects = () => {
  return fetch(`http://localhost:5000/api/project?`, {
    headers,
    method: "GET",
  })
  .then(response => response.json())
  .then(data => data.content)    
};

export const uploadImages = (data) => {
  var urls = []
  data.pictures.forEach((file) => {
    let formData = new FormData();
    formData.append('file', file);
    fetch("http://localhost:5000/api/project/uploadImage", 
    {
      headers: formImageUploadHeaders,
      method: "POST",
      body: formData
    }).then((res) => {
      if (res.ok) {
        res.json().then((response) => {
          urls.push(response.url)
          if(urls.length === data.pictures.length){
            uploadThumbnail(urls, data)
          }
        });
      } else if (res.status == 401) {
        alert("Oops! ");
      }
    }, function (e) {
      alert("Error submitting form!");
    });
  })
}

export const uploadThumbnail = (urls, data) => {
  let formData = new FormData();
  formData.append('file', data.thumbnail);
  fetch("http://localhost:5000/api/project/uploadImage", 
  {
    headers: formImageUploadHeaders,
    method: "POST",
    body: formData
  }).then((res) => {
    if (res.ok) {
      res.json().then((response) => {
        createProject(urls, response.url, data.title, data.description, data.type)
      });
    } else if (res.status == 401) {
      alert("Oops! ");
    }
  }, function (e) {
    alert("Error submitting form!");
  });
}

export const createProject = (pictures, thumbnail, title, description, type) => {
  if(localStorage.getItem(ACCESS_TOKEN)) {
    headers.append('Authorization', 'Bearer ' + localStorage.getItem(ACCESS_TOKEN))
  }
  let project = {
    'title': title,
    'type': type,
    'thumbnail': thumbnail,
    'description': description,
    'picutures': pictures
  }
  fetch("http://localhost:5000/api/project/uploadproject", 
  {
    headers: headers,
    method: "POST",
    body: JSON.stringify(project)
  }).then(function (res) {
    if (res.ok) {
      alert("Perfect! ");
    } else if (res.status == 401) {
      alert("Oops! ");
    }
  }, function (e) {
    alert("Error submitting form!");
  });
}
