import React from 'react';
import Background from '../assets/background1.png';
import InstagramProfilePhone from '../assets/instagramProfileMobile.png';
import Grid from '@material-ui/core/Grid';
import Button from '@material-ui/core/Button';

const styles = {
  showcase : {
    height: '80vh',
    backgroundImage: `url(${Background})`,
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
    backgroundSize: 'cover'
  },
  overlay: {
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
    height: '100%',
    width: '100%',
  },
  showcaseBannerText: {
    fontSize: 46,
    fontFamily: 'Roboto Mono',
    color:'#FFFFFF',
  },

  showcaseContent: {
    maxWidth: 1000,
    marginLeft: 'auto',
    marginRight: 'auto',
    paddingRight: 40,
    paddingLeft: 40,
    height: '100%',
    display: 'flex',
    alignItems: 'center',
  },
  showCaseButton:{
    marginRight: 20
  },
  showCaseContentLeft:{
    backgroundImage: `url(${InstagramProfilePhone})`,
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
    backgroundSize: 'cover',
    height: '100%',
  },
  showCaseContentRight:{
    height: '100%',
    paddingTop: 250,
    paddingLeft: 100
  },
};

const Showcase = () => (
        <div style = {styles.showcase}> 
          <div style = {styles.overlay}>
            <Grid container spacing ={0} style={styles.showcaseContent}>
              <Grid item xs={12} sm={6} style={styles.showCaseContentLeft}>
              </Grid>
              <Grid item xs={12} sm={6} style={styles.showCaseContentRight}>
                <Grid container spacing ={0}>
                  <Grid item xs={12}>
                    <h1 style={styles.showcaseBannerText}>Christopher Ho Portfolio</h1>
                  </Grid>
                </Grid>
                <Grid container spacing ={0}>
                  <Grid item xs={3}>
                    <Button 
                    variant="contained" 
                    color="primary" 
                    style={styles.showCaseButton}>
                      Resume
                    </Button>
                  </Grid>
                  <Grid item xs={3}>
                    <Button 
                    variant="contained" 
                    color="primary" 
                    style={styles.showCaseButton}>
                      Github
                    </Button>
                  </Grid>
                </Grid>
              </Grid>
            </Grid>
          </div>
        </div>
);

export default Showcase;
