import React from 'react';
import { connect } from 'react-redux';
import TextField from '@material-ui/core/TextField';
import Paper from '@material-ui/core/Paper';
import Grid from '@material-ui/core/Grid';
import {DropzoneArea} from 'material-ui-dropzone';
import Button from '@material-ui/core/Button';
import {uploadImages} from '../../utils/api';
import CircularProgress from '@material-ui/core/CircularProgress';

const styles = {
  paper: {
    maxWidth: 700,
    marginLeft: 'auto',
    marginRight: 'auto',
    paddingRight: 40,
    paddingLeft: 40,
  },
  field: {
    marginRight: 20,
    marginLeft: 20
  },
  container:{
    marginRight: 40,
    marginLeft: 40,
    position: 'relative',
  },
  dropzone:{
    marginTop: 20,
  },
  submitButton:{
    marginTop: 20,
    marginBottom: 20,
  },
  thumbnailUpload:{
    marginTop:10,
    flexDirection: 'row'
  },
  title:{
    textAlign:'center'
  },
  overlay:{
    opacity: 0.5 ,
    background: "#000",
    width: "100%",
    height: "100%", 
    zIndex:  10,
    top: 0, 
    left: 0, 
    position: "fixed", 
    textAlign: "center"
  },
  progressSpineer:{
    position: "absolute",
    top:0,
    bottom: 0,
    left: 0,
    right: 0,
    margin: 'auto',
    width: 100,
    height: 100,
  }
};

export class ProjectForm extends React.Component{
  constructor(props) {
    super(props);
    this.state = {
      title: '',
      type: '',
      thumbnail: '',
      description: '',
      pictures: []
    };
  }

  handleChange = fieldName => event => {
    this.setState({ 
      [fieldName] : event.target.value
    })
  };

  handleThumbnail = fieldName => event => { 
    this.setState({ 
      [fieldName] : event.target.files[0]
    })
  }

  handleImageDrop = fieldName => image => {
    var files = this.state.pictures
    files.push(image)
    this.setState({ 
      [fieldName] : files
    })
  }

  render(){
    return (
      <div>
        <div style={styles.overlay}> 
        <CircularProgress color="secondary" />
        </div>
        <Grid container spacing = {24}>
          <Grid item xs={12} style={styles.container}>
            <div style={styles.title}>
              <h2>Create Project</h2>
            </div> 
            <Paper style = {styles.paper}>
              <Grid item xs= {12}>
                <TextField
                  id="outlined-name"
                  label="Name"
                  value={this.state.name}
                  onChange={this.handleChange('title')}
                  margin="normal"
                  variant="outlined"
                  fullWidth={true}
                />
              </Grid>
              <Grid item xs= {12}>
                <TextField
                  id="outlined-type"
                  label="Type"
                  value={this.state.type}
                  onChange={this.handleChange('type')}
                  margin="normal"
                  variant="outlined"
                  multiline={true}
                  fullWidth={true}
                />
              </Grid>
              <Grid item xs={12} style={styles.thumbnailUpload}>
                <Button variant="contained"component="label">
                  Upload Thumbnail
                  <input
                    type="file"
                    style={{ display: "none" }}
                    onChange={this.handleThumbnail('thumbnail')}
                  />
                </Button>
                <p>{this.state.thumbnail.name}</p>
              </Grid>
              <Grid item xs= {12}>
                <TextField
                  id="outlined-description"
                  label="Description"
                  value={this.state.description}
                  onChange={this.handleChange('description')}
                  margin="normal"
                  variant="outlined"
                  multiline={true}
                  fullWidth={true}
                />
              </Grid>
              <Grid item xs= {12} style={styles.dropzone}>
                <DropzoneArea 
                  filesLimit={6}
                  onDrop={this.handleImageDrop('pictures')}
                  />
              </Grid>
              <Grid item xs= {12}>
                <Button 
                variant="contained" 
                color="secondary" 
                style={styles.submitButton}
                onClick={() => {uploadImages(this.state)}}>
                  Create Project
                </Button>
              </Grid>
            </Paper>
          </Grid>
        </Grid>
      </div>
    );
  }
}

export default connect(null,null)(ProjectForm);
